import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";


const CLOUDINARY_UPLOAD_PRESET = "fgl3bmtq";
const CLOUDINARY_CLOUD_NAME = "dqqwrgmo9";

const uploadToCloudinary = async (file) => {
  const formData = new FormData();
  formData.append("file", file);
  formData.append("upload_preset", CLOUDINARY_UPLOAD_PRESET);

  const res = await axios.post(
    `https://api.cloudinary.com/v1_1/${CLOUDINARY_CLOUD_NAME}/image/upload`,
    formData
  );

  return res.data.secure_url;
};


const Admin = () => {
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState("products");
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(false);

  const [productForm, setProductForm] = useState({
    name: "",
    price: "",
    quantity: "",
    categoryName: "",
    categoryId: "",
    imageUrl: "",
  });

  const [categoryForm, setCategoryForm] = useState({
    name: "",
    imageUrl: "",
  });

  const token = localStorage.getItem("token");

  useEffect(() => {
    if (activeTab === "products") {
      fetchProducts();
      fetchCategories();
    }
    if (activeTab === "categories") fetchCategories();
    if (activeTab === "orders") fetchOrders();
  }, [activeTab]);

  const fetchProducts = async () => {
    const res = await axios.get("http://localhost:8080/api/admin/products", {
      headers: { Authorization: `Bearer ${token}` },
    });
    setProducts(res.data);
  };

  const fetchCategories = async () => {
    const res = await axios.get("http://localhost:8080/api/admin/categories", {
      headers: { Authorization: `Bearer ${token}` },
    });
    setCategories(res.data);
  };

  const fetchOrders = async () => {
    const res = await axios.get("http://localhost:8080/api/admin/orders", {
      headers: { Authorization: `Bearer ${token}` },
    });
    setOrders(res.data);
  };


  const handleProductImageUpload = async (file) => {
    setLoading(true);
    const url = await uploadToCloudinary(file);
    setProductForm((p) => ({ ...p, imageUrl: url }));
    setLoading(false);
  };

  const handleCategoryImageUpload = async (file) => {
    setLoading(true);
    const url = await uploadToCloudinary(file);
    setCategoryForm((c) => ({ ...c, imageUrl: url }));
    setLoading(false);
  };

  const handleProductSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    await axios.post(
      "http://localhost:8080/api/admin/product",
      productForm,
      { headers: { Authorization: `Bearer ${token}` } }
    );

    alert("✅ Product added");
    setProductForm({
      name: "",
      price: "",
      quantity: "",
      categoryName: "",
      categoryId: "",
      imageUrl: "",
    });
    fetchProducts();
    setLoading(false);
  };

  const handleCategorySubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    await axios.post(
      "http://localhost:8080/api/admin/category",
      categoryForm,
      { headers: { Authorization: `Bearer ${token}` } }
    );

    alert("✅ Category added");
    setCategoryForm({ name: "", imageUrl: "" });
    fetchCategories();
    setLoading(false);
  };

  const handleLogout = () => {
    localStorage.clear();
    navigate("/signin");
  };

  return (
    <div className="flex min-h-screen bg-[#f8fafc]">
    
      <aside className="w-[260px] bg-[#0f0c29] text-white p-6 fixed h-full">
        <h1 className="text-2xl font-black mb-8">Admin Panel</h1>

        {["products", "categories", "orders"].map((tab) => (
          <button
            key={tab}
            onClick={() => setActiveTab(tab)}
            className={`w-full mb-3 py-3 rounded-xl font-semibold ${
              activeTab === tab ? "bg-[#667eea]" : "bg-white/10"
            }`}
          >
            {tab.toUpperCase()}
          </button>
        ))}

        <button
          onClick={handleLogout}
          className="mt-10 w-full py-3 rounded-xl bg-red-500"
        >
          Logout
        </button>
      </aside>


      <main className="ml-[260px] flex-1 p-10">
        
        {activeTab === "products" && (
          <form
            onSubmit={handleProductSubmit}
            className="bg-white p-8 rounded-3xl shadow max-w-3xl"
          >
            <h2 className="text-3xl font-black mb-6">Add Product</h2>

            <input
              placeholder="Product Name"
              value={productForm.name}
              onChange={(e) =>
                setProductForm({ ...productForm, name: e.target.value })
              }
              className="input"
              required
            />

            <input
              type="number"
              placeholder="Price"
              value={productForm.price}
              onChange={(e) =>
                setProductForm({ ...productForm, price: e.target.value })
              }
              className="input"
              required
            />

            <input
              type="number"
              placeholder="Quantity"
              value={productForm.quantity}
              onChange={(e) =>
                setProductForm({ ...productForm, quantity: e.target.value })
              }
              className="input"
            />

            <select
              value={productForm.categoryName}
              onChange={(e) =>
                setProductForm({
                  ...productForm,
                  categoryName: e.target.value,
                })
              }
              className="input"
              required
            >
              <option value="">Select Category</option>
              {categories.map((c) => (
                <option key={c.id} value={c.name}>
                  {c.name}
                </option>
              ))}
            </select>

            <input
              type="file"
              accept="image/*"
              onChange={(e) => handleProductImageUpload(e.target.files[0])}
              className="mt-4"
            />

            {productForm.imageUrl && (
              <img
                src={productForm.imageUrl}
                alt="preview"
                className="mt-4 h-40 rounded-xl object-cover"
              />
            )}

            <button
              disabled={loading}
              className="mt-6 w-full py-4 bg-[#667eea] text-white font-bold rounded-xl"
            >
              {loading ? "Uploading..." : "Add Product"}
            </button>
          </form>
        )}

        {/* ================= CATEGORIES ================= */}
        {activeTab === "categories" && (
          <form
            onSubmit={handleCategorySubmit}
            className="bg-white p-8 rounded-3xl shadow max-w-3xl"
          >
            <h2 className="text-3xl font-black mb-6">Add Category</h2>

            <input
              placeholder="Category Name"
              value={categoryForm.name}
              onChange={(e) =>
                setCategoryForm({ ...categoryForm, name: e.target.value })
              }
              className="input"
              required
            />

            <input
              type="file"
              accept="image/*"
              onChange={(e) => handleCategoryImageUpload(e.target.files[0])}
              className="mt-4"
            />

            {categoryForm.imageUrl && (
              <img
                src={categoryForm.imageUrl}
                alt="preview"
                className="mt-4 h-40 rounded-xl object-cover"
              />
            )}

            <button
              disabled={loading}
              className="mt-6 w-full py-4 bg-[#667eea] text-white font-bold rounded-xl"
            >
              {loading ? "Uploading..." : "Add Category"}
            </button>
          </form>
        )}
      </main>
    </div>
  );
};

export default Admin;
