import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import Navbar from '../Components/Navbar';
import Footer from '../Components/Footer';
import '../styles/ProductDetail.css';

const ProductDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [product, setProduct] = useState(null);
  const [quantity, setQuantity] = useState(1);
  const [loading, setLoading] = useState(true);
  const [orderLoading, setOrderLoading] = useState(false);
  const [orderSuccess, setOrderSuccess] = useState(false);

  useEffect(() => {
    fetchProduct();
  }, [id]);

  const fetchProduct = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get(`/api/user/products/${id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setProduct(response.data);
    } catch (error) {
      console.error('Error fetching product:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleCheckout = async () => {
    setOrderLoading(true);
    try {
      const token = localStorage.getItem('token');
      const orderData = {
        productId: product.id,
        quantity: quantity,
        totalAmount: product.price * quantity
      };

      await axios.post('/api/user/orders', orderData, {
        headers: { Authorization: `Bearer ${token}` }
      });

      setOrderSuccess(true);
      setTimeout(() => {
        navigate('/ecommerce');
      }, 2000);
    } catch (error) {
      console.error('Error creating order:', error);
      alert('Failed to place order. Please try again.');
    } finally {
      setOrderLoading(false);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('role');
    navigate('/signin');
  };

  if (loading) {
    return (
      <div className="ecommerce-page">
        <Navbar onLogout={handleLogout} />
        <div className="loading-container">
          <div className="spinner"></div>
        </div>
      </div>
    );
  }

  if (!product) {
    return (
      <div className="ecommerce-page">
        <Navbar onLogout={handleLogout} />
        <div className="error-container">
          <h2>Product not found</h2>
          <button onClick={() => navigate('/ecommerce')}>Back to Shop</button>
        </div>
      </div>
    );
  }

  return (
    <div className="ecommerce-page">
      <Navbar onLogout={handleLogout} />
      
      <div className="container product-detail-container">
        <button className="back-button" onClick={() => navigate('/ecommerce')}>
          <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
            <line x1="19" y1="12" x2="5" y2="12"/>
            <polyline points="12 19 5 12 12 5"/>
          </svg>
          Back to Shop
        </button>

        <div className="product-detail-grid">
          <div className="product-detail-image">
            {product.image ? (
              <img src={product.image} alt={product.name} />
            ) : (
              <div className="product-placeholder-large">
                <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                  <rect x="3" y="3" width="18" height="18" rx="2" ry="2"/>
                  <circle cx="8.5" cy="8.5" r="1.5"/>
                  <polyline points="21 15 16 10 5 21"/>
                </svg>
              </div>
            )}
          </div>

          <div className="product-detail-info">
            <div className="product-detail-header">
              <h1 className="product-detail-title">{product.name}</h1>
              {product.badge && (
                <span className="product-detail-badge">{product.badge}</span>
              )}
            </div>

            <div className="product-detail-price">
              <span className="price-label">Price</span>
              <span className="price-value">${product.price}</span>
            </div>

            <div className="product-detail-description">
              <h3>Description</h3>
              <p>{product.description || 'No description available'}</p>
            </div>

            {product.features && product.features.length > 0 && (
              <div className="product-features">
                <h3>Features</h3>
                <ul>
                  {product.features.map((feature, index) => (
                    <li key={index}>
                      <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                        <polyline points="20 6 9 17 4 12"/>
                      </svg>
                      {feature}
                    </li>
                  ))}
                </ul>
              </div>
            )}

            <div className="product-quantity">
              <h3>Quantity</h3>
              <div className="quantity-controls">
                <button 
                  onClick={() => setQuantity(Math.max(1, quantity - 1))}
                  disabled={quantity <= 1}
                >
                  <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                    <line x1="5" y1="12" x2="19" y2="12"/>
                  </svg>
                </button>
                <span className="quantity-value">{quantity}</span>
                <button onClick={() => setQuantity(quantity + 1)}>
                  <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                    <line x1="12" y1="5" x2="12" y2="19"/>
                    <line x1="5" y1="12" x2="19" y2="12"/>
                  </svg>
                </button>
              </div>
            </div>

            <div className="product-total">
              <span>Total</span>
              <span className="total-amount">${(product.price * quantity).toFixed(2)}</span>
            </div>

            <button 
              className="checkout-button"
              onClick={handleCheckout}
              disabled={orderLoading || orderSuccess}
            >
              {orderLoading ? (
                <>
                  <span className="button-loader"></span>
                  Processing...
                </>
              ) : orderSuccess ? (
                <>
                  <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                    <polyline points="20 6 9 17 4 12"/>
                  </svg>
                  Order Placed!
                </>
              ) : (
                <>
                  <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                    <circle cx="9" cy="21" r="1"/>
                    <circle cx="20" cy="21" r="1"/>
                    <path d="M1 1h4l2.68 13.39a2 2 0 0 0 2 1.61h9.72a2 2 0 0 0 2-1.61L23 6H6"/>
                  </svg>
                  Checkout
                </>
              )}
            </button>
          </div>
        </div>
      </div>

      <Footer />
    </div>
  );
};

export default ProductDetail;
