import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';

const SignUp = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    role: 'ROLE_USER'
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
    setError('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      await axios.post('/api/auth/register', formData);
      navigate('/signin');
    } catch (err) {
      setError(err.response?.data?.message || 'Registration failed. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center relative overflow-hidden bg-gradient-to-br from-[#0f0c29] via-[#302b63] to-[#24243e]">
      {/* Animated Background Orbs */}
      <div className="absolute inset-0 overflow-hidden">
        <div className="absolute w-[500px] h-[500px] -top-[200px] -left-[200px] rounded-full bg-gradient-to-br from-[#667eea] to-[#764ba2] opacity-30 blur-[100px] animate-float"></div>
        <div className="absolute w-[400px] h-[400px] -bottom-[150px] -right-[150px] rounded-full bg-gradient-to-br from-[#f093fb] to-[#f5576c] opacity-30 blur-[100px] animate-float-delayed"></div>
        <div className="absolute w-[350px] h-[350px] top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 rounded-full bg-gradient-to-br from-[#4facfe] to-[#00f2fe] opacity-30 blur-[100px] animate-float-slow"></div>
      </div>

      {/* Auth Card */}
      <div className="relative z-10 w-full max-w-md px-5">
        <div className="bg-white/95 backdrop-blur-xl rounded-3xl p-12 shadow-2xl border border-white/20 animate-slide-up">
          {/* Header */}
          <div className="text-center mb-10">
            <h1 className="font-['Playfair_Display'] text-5xl font-black text-[#1a1a2e] mb-3 tracking-tight">
              Create Account
            </h1>
            <p className="text-[#64748b] text-base">Start your shopping experience today</p>
          </div>

          {/* Form */}
          <form onSubmit={handleSubmit} className="space-y-6">
            {/* Error Message */}
            {error && (
              <div className="flex items-center gap-3 p-4 bg-gradient-to-r from-red-100 to-red-200 text-red-800 rounded-xl text-sm font-medium border border-red-300">
                <svg className="w-5 h-5 flex-shrink-0" viewBox="0 0 20 20" fill="currentColor">
                  <path d="M10 0C4.48 0 0 4.48 0 10s4.48 10 10 10 10-4.48 10-10S15.52 0 10 0zm1 15H9v-2h2v2zm0-4H9V5h2v6z"/>
                </svg>
                {error}
              </div>
            )}

            {/* Name Field */}
            <div className="space-y-2">
              <label htmlFor="name" className="block text-sm font-semibold text-[#334155] tracking-wide">
                Full Name
              </label>
              <input
                type="text"
                id="name"
                name="name"
                value={formData.name}
                onChange={handleChange}
                className="w-full px-5 py-3.5 border-2 border-[#e2e8f0] rounded-xl text-[15px] text-[#1e293b] transition-all duration-300 focus:outline-none focus:border-[#667eea] focus:shadow-[0_0_0_4px_rgba(102,126,234,0.1)] focus:-translate-y-0.5"
                placeholder="John Doe"
                required
              />
            </div>

            {/* Email Field */}
            <div className="space-y-2">
              <label htmlFor="email" className="block text-sm font-semibold text-[#334155] tracking-wide">
                Email Address
              </label>
              <input
                type="email"
                id="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                className="w-full px-5 py-3.5 border-2 border-[#e2e8f0] rounded-xl text-[15px] text-[#1e293b] transition-all duration-300 focus:outline-none focus:border-[#667eea] focus:shadow-[0_0_0_4px_rgba(102,126,234,0.1)] focus:-translate-y-0.5"
                placeholder="you@example.com"
                required
              />
            </div>

            {/* Password Field */}
            <div className="space-y-2">
              <label htmlFor="password" className="block text-sm font-semibold text-[#334155] tracking-wide">
                Password
              </label>
              <input
                type="password"
                id="password"
                name="password"
                value={formData.password}
                onChange={handleChange}
                className="w-full px-5 py-3.5 border-2 border-[#e2e8f0] rounded-xl text-[15px] text-[#1e293b] transition-all duration-300 focus:outline-none focus:border-[#667eea] focus:shadow-[0_0_0_4px_rgba(102,126,234,0.1)] focus:-translate-y-0.5"
                placeholder="••••••••"
                required
                minLength="6"
              />
            </div>

            {/* Role Field */}
            <div className="space-y-2">
              <label htmlFor="role" className="block text-sm font-semibold text-[#334155] tracking-wide">
                Account Type
              </label>
              <select
                id="role"
                name="role"
                value={formData.role}
                onChange={handleChange}
                className="w-full px-5 py-3.5 border-2 border-[#e2e8f0] rounded-xl text-[15px] text-[#1e293b] transition-all duration-300 focus:outline-none focus:border-[#667eea] focus:shadow-[0_0_0_4px_rgba(102,126,234,0.1)] focus:-translate-y-0.5 bg-white"
                required
              >
                <option value="ROLE_USER">Customer</option>
                <option value="ROLE_ADMIN">Administrator</option>
              </select>
            </div>

            {/* Submit Button */}
            <button
              type="submit"
              disabled={loading}
              className="w-full mt-8 px-4 py-4 bg-gradient-to-r from-[#667eea] to-[#764ba2] text-white rounded-xl text-base font-bold transition-all duration-300 shadow-[0_4px_15px_rgba(102,126,234,0.4)] hover:-translate-y-0.5 hover:shadow-[0_6px_20px_rgba(102,126,234,0.5)] disabled:opacity-60 disabled:cursor-not-allowed disabled:transform-none tracking-wide"
            >
              {loading ? (
                <span className="inline-block w-5 h-5 border-3 border-white/30 border-t-white rounded-full animate-spin"></span>
              ) : (
                'Create Account'
              )}
            </button>
          </form>

          {/* Footer */}
          <div className="mt-8 text-center text-sm text-[#64748b]">
            <p>
              Already have an account?{' '}
              <Link to="/signin" className="text-[#667eea] font-semibold hover:text-[#764ba2] hover:underline transition-colors">
                Sign In
              </Link>
            </p>
          </div>
        </div>
      </div>

      <style jsx>{`
        @keyframes float {
          0%, 100% { transform: translateY(0) translateX(0) scale(1); }
          33% { transform: translateY(-30px) translateX(30px) scale(1.1); }
          66% { transform: translateY(30px) translateX(-30px) scale(0.9); }
        }
        @keyframes slide-up {
          from { opacity: 0; transform: translateY(30px); }
          to { opacity: 1; transform: translateY(0); }
        }
        .animate-float { animation: float 20s infinite ease-in-out; }
        .animate-float-delayed { animation: float 20s infinite ease-in-out 5s; }
        .animate-float-slow { animation: float 20s infinite ease-in-out 10s; }
        .animate-slide-up { animation: slide-up 0.6s ease-out; }
      `}</style>
    </div>
  );
};

export default SignUp;
