import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const Admin = () => {
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState('products');
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(false);

  const [productForm, setProductForm] = useState({
    name: '', price: '', categoryName: '', imageUrl: '',categoryId:"",quantity:""
  });

  const [categoryForm, setCategoryForm] = useState({ name: '', imageUrl: '' });

  useEffect(() => {
    if (activeTab === 'products') { fetchProducts(); fetchCategories(); }
    else if (activeTab === 'categories') fetchCategories();
    else if (activeTab === 'orders') fetchOrders();
  }, [activeTab]);

  const fetchProducts = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:8080/api/admin/products', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setProducts(response.data);
    } catch (error) {
      console.error('Error fetching products:', error);
    }
  };

  const fetchCategories = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:8080/api/admin/categories', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setCategories(response.data);
    } catch (error) {
      console.error('Error fetching categories:', error);
    }
  };

  const fetchOrders = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:8080/api/admin/orders', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setOrders(response.data);
    } catch (error) {
      console.error('Error fetching orders:', error);
    }
  };

  const handleProductSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      await axios.post('http://localhost:8080/api/admin/products', productForm, {
        headers: { Authorization: `Bearer ${token}` }
      });
      alert('Product added successfully!');
      setProductForm({ name: '', description: '', price: '', category: '', image: ''});
      fetchProducts();
    } catch (error) {
      console.error('Error adding product:', error);
      alert('Failed to add product');
    } finally {
      setLoading(false);
    }
  };

  const handleCategorySubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      alert(token)
      alert(categoryForm.name)
      await axios.post('http://localhost:8080/api/admin/category', {name:categoryForm.name,imageUrl:categoryForm.imageUrl}, {
        headers: { Authorization: `Bearer ${token}` }
      });
      alert('Category added successfully!');
      setCategoryForm({ name: '', imageUrl: '' });
      fetchCategories();
    } catch (error) {
      console.error('Error adding category:', error);
      alert('Failed to add category');
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('role');
    navigate('/signin');
  };

  return (
    <div className="flex min-h-screen bg-[#f8fafc]">
      {/* Sidebar */}
      <aside className="w-[280px] bg-gradient-to-b from-[#1a1a2e] to-[#0f0c29] border-r border-white/10 flex flex-col fixed left-0 top-0 bottom-0 z-[1000]">
        {/* Logo */}
        <div className="p-8 border-b border-white/10">
          <div className="flex items-center gap-3 text-white">
            <svg className="w-8 h-8 stroke-[#667eea]" viewBox="0 0 24 24" fill="none" strokeWidth="2">
              <circle cx="12" cy="12" r="10"/>
              <path d="M12 6v6l4 2"/>
            </svg>
            <span className="font-['Playfair_Display'] text-2xl font-black tracking-tight">Admin Panel</span>
          </div>
        </div>

        {/* Navigation */}
        <nav className="flex-1 p-6 space-y-2">
          <button
            onClick={() => setActiveTab('products')}
            className={`w-full flex items-center gap-3.5 px-5 py-4 rounded-xl text-[15px] font-semibold transition-all duration-300 tracking-wide ${
              activeTab === 'products' 
                ? 'bg-gradient-to-r from-[#667eea] to-[#764ba2] text-white shadow-[0_4px_15px_rgba(102,126,234,0.3)]' 
                : 'bg-transparent text-[#94a3b8] hover:bg-white/5 hover:text-[#e2e8f0]'
            }`}
          >
            <svg className="w-5.5 h-5.5 flex-shrink-0" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
              <rect x="2" y="7" width="20" height="14" rx="2" ry="2"/>
              <path d="M16 21V5a2 2 0 0 0-2-2h-4a2 2 0 0 0-2 2v16"/>
            </svg>
            Add Products
          </button>

          <button
            onClick={() => setActiveTab('categories')}
            className={`w-full flex items-center gap-3.5 px-5 py-4 rounded-xl text-[15px] font-semibold transition-all duration-300 tracking-wide ${
              activeTab === 'categories' 
                ? 'bg-gradient-to-r from-[#667eea] to-[#764ba2] text-white shadow-[0_4px_15px_rgba(102,126,234,0.3)]' 
                : 'bg-transparent text-[#94a3b8] hover:bg-white/5 hover:text-[#e2e8f0]'
            }`}
          >
            <svg className="w-5.5 h-5.5 flex-shrink-0" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
              <rect x="3" y="3" width="7" height="7"/>
              <rect x="14" y="3" width="7" height="7"/>
              <rect x="14" y="14" width="7" height="7"/>
              <rect x="3" y="14" width="7" height="7"/>
            </svg>
            Add Categories
          </button>

          <button
            onClick={() => setActiveTab('orders')}
            className={`w-full flex items-center gap-3.5 px-5 py-4 rounded-xl text-[15px] font-semibold transition-all duration-300 tracking-wide ${
              activeTab === 'orders' 
                ? 'bg-gradient-to-r from-[#667eea] to-[#764ba2] text-white shadow-[0_4px_15px_rgba(102,126,234,0.3)]' 
                : 'bg-transparent text-[#94a3b8] hover:bg-white/5 hover:text-[#e2e8f0]'
            }`}
          >
            <svg className="w-5.5 h-5.5 flex-shrink-0" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
              <path d="M6 2L3 6v14a2 2 0 0 0 2 2h14a2 2 0 0 0 2-2V6l-3-4z"/>
              <line x1="3" y1="6" x2="21" y2="6"/>
              <path d="M16 10a4 4 0 0 1-8 0"/>
            </svg>
            All Orders
          </button>
        </nav>

        {/* Logout */}
        <button
          onClick={handleLogout}
          className="m-4 px-5 py-4 bg-white/5 text-[#e2e8f0] border border-white/10 rounded-xl text-[15px] font-semibold flex items-center gap-3 justify-center transition-all duration-300 hover:bg-red-500/10 hover:border-red-500 hover:text-red-200"
        >
          <svg className="w-5 h-5" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
            <path d="M9 21H5a2 2 0 0 1-2-2V5a2 2 0 0 1 2-2h4"/>
            <polyline points="16 17 21 12 16 7"/>
            <line x1="21" y1="12" x2="9" y2="12"/>
          </svg>
          Logout
        </button>
      </aside>

      {/* Main Content */}
      <main className="ml-[280px] flex-1 p-10 overflow-y-auto">
        {activeTab === 'products' && (
          <div className="max-w-[1200px] mx-auto">
            <div className="mb-10">
              <h1 className="font-['Playfair_Display'] text-[42px] font-black text-[#1a1a2e] mb-2 tracking-tight">Add New Product</h1>
              <p className="text-base text-[#64748b]">Create and manage your product inventory</p>
            </div>

            <form onSubmit={handleProductSubmit} className="bg-white rounded-3xl p-10 shadow-[0_4px_20px_rgba(0,0,0,0.05)]">
              <div className="grid grid-cols-2 gap-6 mb-6">
                <div className="space-y-2">
                  <label className="block text-sm font-semibold text-[#334155] tracking-wide">Product Name *</label>
                  <input type="text" value={productForm.name} onChange={(e) => setProductForm({...productForm, name: e.target.value})} required placeholder="Enter product name" className="w-full px-5 py-3.5 border-2 border-[#e2e8f0] rounded-xl text-[15px] text-[#1e293b] transition-all duration-300 focus:outline-none focus:border-[#667eea] focus:shadow-[0_0_0_4px_rgba(102,126,234,0.1)]" />
                </div>
                <div className="space-y-2">
                  <label className="block text-sm font-semibold text-[#334155] tracking-wide">Price *</label>
                  <input type="number" step="0.01" value={productForm.price} onChange={(e) => setProductForm({...productForm, price: e.target.value})} required placeholder="0.00" className="w-full px-5 py-3.5 border-2 border-[#e2e8f0] rounded-xl text-[15px] text-[#1e293b] transition-all duration-300 focus:outline-none focus:border-[#667eea] focus:shadow-[0_0_0_4px_rgba(102,126,234,0.1)]" />
                </div>
              </div>

              <div className="grid grid-cols-2 gap-6 mb-6">
                <div className="space-y-2">
                  <label className="block text-sm font-semibold text-[#334155] tracking-wide">Category Name*</label>
                  <select value={productForm.categoryName} onChange={(e) => setProductForm({...productForm, categoryName: e.target.value})} required className="w-full px-5 py-3.5 border-2 border-[#e2e8f0] rounded-xl text-[15px] text-[#1e293b] bg-white transition-all duration-300 focus:outline-none focus:border-[#667eea] focus:shadow-[0_0_0_4px_rgba(102,126,234,0.1)]">
                    <option value="">Select category</option>
                    {categories.map((cat) => (<option key={cat.id} value={cat.name}>{cat.name}</option>))}
                  </select>
                </div>
                <div className="space-y-2">
                  <label className="block text-sm font-semibold text-[#334155] tracking-wide">Quantity</label>
                  <input type="number" value={productForm.quantity} onChange={(e) => setProductForm({...productForm, quantity: e.target.value})} placeholder="Available quantity" className="w-full px-5 py-3.5 border-2 border-[#e2e8f0] rounded-xl text-[15px] text-[#1e293b] transition-all duration-300 focus:outline-none focus:border-[#667eea] focus:shadow-[0_0_0_4px_rgba(102,126,234,0.1)]" />
                </div>
              </div>

              {/* <div className="mb-6 space-y-2">
                <label className="block text-sm font-semibold text-[#334155] tracking-wide">Description</label>
                <textarea value={productForm.description} onChange={(e) => setProductForm({...productForm, description: e.target.value})} rows="4" placeholder="Product description" className="w-full px-5 py-3.5 border-2 border-[#e2e8f0] rounded-xl text-[15px] text-[#1e293b] resize-y min-h-[120px] transition-all duration-300 focus:outline-none focus:border-[#667eea] focus:shadow-[0_0_0_4px_rgba(102,126,234,0.1)]" />
              </div> */}

              <div className="grid grid-cols-2 gap-6 mb-6">
                <div className="space-y-2">
                  <label className="block text-sm font-semibold text-[#334155] tracking-wide">Image URL</label>
                  <input type="url" value={productForm.imageUrl} onChange={(e) => setProductForm({...productForm, imageUrl: e.target.value})} placeholder="https://example.com/image.jpg" className="w-full px-5 py-3.5 border-2 border-[#e2e8f0] rounded-xl text-[15px] text-[#1e293b] transition-all duration-300 focus:outline-none focus:border-[#667eea] focus:shadow-[0_0_0_4px_rgba(102,126,234,0.1)]" />
                </div>
                <div className="space-y-2">
                  <label className="block text-sm font-semibold text-[#334155] tracking-wide">Category Id(Optional)</label>
                  <input type="text" value={productForm.categoryId} onChange={(e) => setProductForm({...productForm, categoryId: e.target.value})} placeholder="New, Sale, etc." className="w-full px-5 py-3.5 border-2 border-[#e2e8f0] rounded-xl text-[15px] text-[#1e293b] transition-all duration-300 focus:outline-none focus:border-[#667eea] focus:shadow-[0_0_0_4px_rgba(102,126,234,0.1)]" />
                </div>
              </div>

              <button type="submit" disabled={loading} className="mt-4 px-10 py-4 bg-gradient-to-r from-[#667eea] to-[#764ba2] text-white rounded-xl text-base font-bold transition-all duration-300 shadow-[0_4px_15px_rgba(102,126,234,0.3)] hover:-translate-y-0.5 hover:shadow-[0_6px_20px_rgba(102,126,234,0.4)] disabled:opacity-60 disabled:cursor-not-allowed disabled:transform-none tracking-wide">
                {loading ? 'Adding...' : 'Add Product'}
              </button>
            </form>
          </div>
        )}

        {activeTab === 'categories' && (
          <div className="max-w-[1200px] mx-auto">
            <div className="mb-10">
              <h1 className="font-['Playfair_Display'] text-[42px] font-black text-[#1a1a2e] mb-2 tracking-tight">Add New Category</h1>
              <p className="text-base text-[#64748b]">Organize your products with categories</p>
            </div>

            <form onSubmit={handleCategorySubmit} className="bg-white rounded-3xl p-10 shadow-[0_4px_20px_rgba(0,0,0,0.05)] mb-10">
              <div className="grid grid-cols-2 gap-6 mb-6">
                <div className="space-y-2">
                  <label className="block text-sm font-semibold text-[#334155] tracking-wide">Category Name *</label>
                  <input type="text" value={categoryForm.name} onChange={(e) => setCategoryForm({...categoryForm, name: e.target.value})} required placeholder="Enter category name" className="w-full px-5 py-3.5 border-2 border-[#e2e8f0] rounded-xl text-[15px] text-[#1e293b] transition-all duration-300 focus:outline-none focus:border-[#667eea] focus:shadow-[0_0_0_4px_rgba(102,126,234,0.1)]" />
                </div>
                <div className="space-y-2">
                  <label className="block text-sm font-semibold text-[#334155] tracking-wide">Image url</label>
                  <input type="text" value={categoryForm.imageUrl} onChange={(e) => setCategoryForm({...categoryForm, imageUrl: e.target.value})} placeholder="imageUrl" className="w-full px-5 py-3.5 border-2 border-[#e2e8f0] rounded-xl text-[15px] text-[#1e293b] transition-all duration-300 focus:outline-none focus:border-[#667eea] focus:shadow-[0_0_0_4px_rgba(102,126,234,0.1)]" />
                </div>
              </div>

              <button type="submit" disabled={loading} className="mt-4 px-10 py-4 bg-gradient-to-r from-[#667eea] to-[#764ba2] text-white rounded-xl text-base font-bold transition-all duration-300 shadow-[0_4px_15px_rgba(102,126,234,0.3)] hover:-translate-y-0.5 hover:shadow-[0_6px_20px_rgba(102,126,234,0.4)] disabled:opacity-60 disabled:cursor-not-allowed disabled:transform-none tracking-wide">
                {loading ? 'Adding...' : 'Add Category'}
              </button>
            </form>

            <div className="bg-white rounded-3xl p-10 shadow-[0_4px_20px_rgba(0,0,0,0.05)]">
              <h2 className="text-2xl font-bold text-[#1e293b] mb-6">Existing Categories</h2>
              <div className="grid grid-cols-5 gap-4">
                {categories.map((category) => (
                  <div key={category.id} className="flex items-center gap-3 p-5 bg-gradient-to-br from-[#667eea]/5 to-[#764ba2]/5 rounded-xl border-2 border-[#667eea]/10 transition-all duration-300 hover:-translate-y-0.5 hover:shadow-[0_4px_15px_rgba(102,126,234,0.15)]">
                    <span className="text-3xl">{category.icon}</span>
                    <span className="text-[15px] font-semibold text-[#1e293b]">{category.name}</span>
                  </div>
                ))}
              </div>
            </div>
          </div>
        )}

        {activeTab === 'orders' && (
          <div className="max-w-[1200px] mx-auto">
            <div className="mb-10">
              <h1 className="font-['Playfair_Display'] text-[42px] font-black text-[#1a1a2e] mb-2 tracking-tight">All Orders</h1>
              <p className="text-base text-[#64748b]">Manage customer orders and track shipments</p>
            </div>

            <div className="bg-white rounded-3xl p-10 shadow-[0_4px_20px_rgba(0,0,0,0.05)] overflow-x-auto">
              <table className="w-full border-collapse">
                <thead>
                  <tr className="bg-gradient-to-r from-[#667eea]/5 to-[#764ba2]/5">
                    <th className="px-5 py-4 text-left text-xs font-bold text-[#64748b] uppercase tracking-wider border-b-2 border-[#e2e8f0]">Order ID</th>
                    <th className="px-5 py-4 text-left text-xs font-bold text-[#64748b] uppercase tracking-wider border-b-2 border-[#e2e8f0]">Customer</th>
                    <th className="px-5 py-4 text-left text-xs font-bold text-[#64748b] uppercase tracking-wider border-b-2 border-[#e2e8f0]">Product</th>
                    <th className="px-5 py-4 text-left text-xs font-bold text-[#64748b] uppercase tracking-wider border-b-2 border-[#e2e8f0]">Quantity</th>
                    <th className="px-5 py-4 text-left text-xs font-bold text-[#64748b] uppercase tracking-wider border-b-2 border-[#e2e8f0]">Total</th>
                    <th className="px-5 py-4 text-left text-xs font-bold text-[#64748b] uppercase tracking-wider border-b-2 border-[#e2e8f0]">Status</th>
                    <th className="px-5 py-4 text-left text-xs font-bold text-[#64748b] uppercase tracking-wider border-b-2 border-[#e2e8f0]">Date</th>
                  </tr>
                </thead>
                <tbody>
                  {orders.length === 0 ? (
                    <tr><td colSpan="7" className="px-5 py-16 text-center text-base text-[#64748b]">No orders found</td></tr>
                  ) : (
                    orders.map((order) => (
                      <tr key={order.id} className="transition-colors hover:bg-[#f8fafc]">
                        <td className="px-5 py-5 border-b border-[#f1f5f9] text-sm text-[#475569]">#{order.id}</td>
                        <td className="px-5 py-5 border-b border-[#f1f5f9] text-sm text-[#475569]">{order.customerName || 'N/A'}</td>
                        <td className="px-5 py-5 border-b border-[#f1f5f9] text-sm text-[#475569]">{order.productName || 'N/A'}</td>
                        <td className="px-5 py-5 border-b border-[#f1f5f9] text-sm text-[#475569]">{order.quantity}</td>
                        <td className="px-5 py-5 border-b border-[#f1f5f9] text-sm text-[#475569]">${order.totalAmount}</td>
                        <td className="px-5 py-5 border-b border-[#f1f5f9]">
                          <span className={`inline-block px-3.5 py-1.5 rounded-full text-xs font-bold uppercase tracking-wider ${
                            order.status?.toLowerCase() === 'pending' ? 'bg-gradient-to-r from-yellow-100 to-yellow-200 text-yellow-900' :
                            order.status?.toLowerCase() === 'processing' ? 'bg-gradient-to-r from-blue-100 to-blue-200 text-blue-900' :
                            order.status?.toLowerCase() === 'completed' ? 'bg-gradient-to-r from-green-100 to-green-200 text-green-900' :
                            order.status?.toLowerCase() === 'cancelled' ? 'bg-gradient-to-r from-red-100 to-red-200 text-red-900' :
                            'bg-gradient-to-r from-yellow-100 to-yellow-200 text-yellow-900'
                          }`}>
                            {order.status || 'Pending'}
                          </span>
                        </td>
                        <td className="px-5 py-5 border-b border-[#f1f5f9] text-sm text-[#475569]">{new Date(order.createdAt).toLocaleDateString()}</td>
                      </tr>
                    ))
                  )}
                </tbody>
              </table>
            </div>
          </div>
        )}
      </main>
    </div>
  );
};

export default Admin;
