import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import Navbar from '../Components/Navbar';
import Footer from '../Components/Footer';
import Carousel from '../Components/Carousel';

const Ecommerce = () => {
  const navigate = useNavigate();
  const [categories, setCategories] = useState([]);
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedCategory, setSelectedCategory] = useState('all');

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const token = localStorage.getItem('token');
      const config = {
        headers: { Authorization: `Bearer ${token}` }
      };

      const [categoriesRes, productsRes] = await Promise.all([
        axios.get('http://localhost:8080/api/user/categories', config),
        axios.get('http://localhost:8080/api/user/products', config)
      ]);

      setCategories(categoriesRes.data);
      setProducts(productsRes.data);
    } catch (error) {
      console.error('Error fetching data:', error);
    } finally {
      setLoading(false);
    }
  };

  const filteredProducts = selectedCategory === 'all' 
    ? products 
    : products.filter(product => product.category === selectedCategory);

  const handleProductClick = (productId) => {
    navigate(`/product/${productId}`);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('role');
    navigate('/signin');
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-[#f8fafc] to-[#f1f5f9]">
      <Navbar onLogout={handleLogout} />
      
      <Carousel />

      <div className="max-w-[1400px] mx-auto px-6">
        {/* Categories Section */}
        <section className="py-20">
          <h2 className="font-['Playfair_Display'] text-[42px] font-black text-[#1a1a2e] mb-3 tracking-tight text-center">
            Shop by Category
          </h2>
          
          <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 xl:grid-cols-5 gap-6 mt-10">
            {/* All Products Card */}
            <div
              onClick={() => setSelectedCategory('all')}
              className={`bg-white rounded-2xl p-10 text-center cursor-pointer transition-all duration-[400ms] ease-[cubic-bezier(0.4,0,0.2,1)] border-2 shadow-[0_4px_20px_rgba(0,0,0,0.05)] hover:-translate-y-2 hover:shadow-[0_12px_40px_rgba(102,126,234,0.15)] ${
                selectedCategory === 'all' 
                  ? 'bg-gradient-to-br from-[#667eea] to-[#764ba2] text-white border-[#667eea] shadow-[0_8px_30px_rgba(102,126,234,0.3)]' 
                  : 'border-transparent'
              }`}
            >
              <div className={`w-20 h-20 mx-auto mb-5 rounded-2xl flex items-center justify-center transition-all duration-300 ${
                selectedCategory === 'all' 
                  ? 'bg-white/20 scale-110' 
                  : 'bg-gradient-to-br from-[#667eea]/10 to-[#764ba2]/10'
              }`}>
                <svg className={`w-10 h-10 stroke-2 ${selectedCategory === 'all' ? 'stroke-white' : 'stroke-[#667eea]'}`} viewBox="0 0 24 24" fill="none">
                  <rect x="3" y="3" width="7" height="7"/>
                  <rect x="14" y="3" width="7" height="7"/>
                  <rect x="14" y="14" width="7" height="7"/>
                  <rect x="3" y="14" width="7" height="7"/>
                </svg>
              </div>
              <h3 className={`text-lg font-bold tracking-wide ${selectedCategory === 'all' ? 'text-white' : 'text-[#1e293b]'}`}>
                All Products
              </h3>
            </div>

            {/* Category Cards */}
            {categories.map((category) => (
              <div
                key={category.id}
                onClick={() => navigate(`/category/${category.id}`)}
                className={`bg-white rounded-2xl p-10 text-center cursor-pointer transition-all duration-[400ms] ease-[cubic-bezier(0.4,0,0.2,1)] border-2 shadow-[0_4px_20px_rgba(0,0,0,0.05)] hover:-translate-y-2 hover:shadow-[0_12px_40px_rgba(102,126,234,0.15)] ${
                  selectedCategory === category.name 
                    ? 'bg-gradient-to-br from-[#667eea] to-[#764ba2] text-white border-[#667eea] shadow-[0_8px_30px_rgba(102,126,234,0.3)]' 
                    : 'border-transparent'
                }`}
              >
                <div className={`w-20 h-20 mx-auto mb-5 rounded-2xl flex items-center justify-center text-4xl transition-all duration-300 ${
                  selectedCategory === category.name 
                    ? 'bg-white/20 scale-110' 
                    : 'bg-gradient-to-br from-[#667eea]/10 to-[#764ba2]/10'
                }`}>
                  {category.icon || 'ðŸ“¦'}
                </div>
                <h3 className={`text-lg font-bold tracking-wide ${selectedCategory === category.name ? 'text-white' : 'text-[#1e293b]'}`}>
                  {category.name}
                </h3>
              </div>
            ))}
          </div>
        </section>

        {/* Products Section */}
        <section className="py-16 pb-24">
          <div className="text-center mb-16">
            <h2 className="font-['Playfair_Display'] text-[42px] font-black text-[#1a1a2e] mb-3 tracking-tight">
              Featured Products
            </h2>
            <p className="text-lg text-[#64748b]">Discover our latest collection</p>
          </div>

          {loading ? (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
              {[...Array(6)].map((_, i) => (
                <div key={i} className="bg-white rounded-3xl h-[450px] animate-pulse"></div>
              ))}
            </div>
          ) : filteredProducts.length === 0 ? (
            <div className="text-center py-20">
              <svg className="w-32 h-32 mx-auto mb-6 stroke-[#cbd5e1] stroke-2" viewBox="0 0 24 24" fill="none">
                <circle cx="12" cy="12" r="10"/>
                <line x1="12" y1="8" x2="12" y2="12"/>
                <line x1="12" y1="16" x2="12.01" y2="16"/>
              </svg>
              <p className="text-lg text-[#64748b]">No products found</p>
            </div>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
              {filteredProducts.map((product) => (
                <div
                  key={product.id}
                  onClick={() => handleProductClick(product.id)}
                  className="bg-white rounded-3xl overflow-hidden cursor-pointer transition-all duration-[400ms] ease-[cubic-bezier(0.4,0,0.2,1)] shadow-[0_4px_20px_rgba(0,0,0,0.05)] border border-black/5 hover:-translate-y-3 hover:shadow-[0_20px_60px_rgba(0,0,0,0.12)]"
                >
                  {/* Product Image */}
                  <div className="relative w-full h-[280px] bg-gradient-to-br from-[#f8fafc] to-[#e2e8f0] overflow-hidden group">
                    {product.image ? (
                      <img 
                        src={product.imageUrl} 
                        alt={product.name}
                        className="w-full h-full object-cover transition-transform duration-[600ms] group-hover:scale-110"
                      />
                    ) : (
                      <div className="w-full h-full flex items-center justify-center bg-gradient-to-br from-[#667eea]/5 to-[#764ba2]/5">
                        <svg className="w-20 h-20 stroke-[#cbd5e1] stroke-2" viewBox="0 0 24 24" fill="none">
                          <rect x="3" y="3" width="18" height="18" rx="2" ry="2"/>
                          <circle cx="8.5" cy="8.5" r="1.5"/>
                          <polyline points="21 15 16 10 5 21"/>
                        </svg>
                      </div>
                    )}
                    {product.badge && (
                      <span className="absolute top-4 right-4 bg-gradient-to-r from-[#f093fb] to-[#f5576c] text-white px-4 py-2 rounded-full text-xs font-bold uppercase tracking-wider shadow-[0_4px_15px_rgba(245,87,108,0.4)]">
                        {product.badge}
                      </span>
                    )}
                  </div>

                  {/* Product Info */}
                  <div className="p-7">
                    <h3 className="text-[22px] font-bold text-[#1e293b] mb-3 tracking-tight">
                      {product.name}
                    </h3>
                    <p className="text-sm text-[#64748b] mb-6 leading-relaxed line-clamp-2">
                      {product.description}
                    </p>

                    <div className="flex items-center justify-between pt-5 border-t border-[#f1f5f9]">
                      <span className="font-['Playfair_Display'] text-[28px] font-black text-[#667eea]">
                        ${product.price}
                      </span>
                      <button className="flex items-center gap-2 px-6 py-3 bg-gradient-to-r from-[#667eea] to-[#764ba2] text-white rounded-xl text-sm font-semibold transition-all duration-300 hover:translate-x-1 hover:shadow-[0_4px_15px_rgba(102,126,234,0.4)] tracking-wide">
                        View Details
                        <svg className="w-4 h-4" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                          <line x1="5" y1="12" x2="19" y2="12"/>
                          <polyline points="12 5 19 12 12 19"/>
                        </svg>
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </section>
      </div>

      <Footer />
    </div>
  );
};

export default Ecommerce;
